INSERT INTO `product` (`id`, `name`, `price`, `preview`, `descriptionShort`, `descriptionMore`, `category`, `manufacturer_id`, `is_new`) VALUES
(52, 'Игровое кресло Chaos Guard 400M Black&White', '13399.00', '/uploads/XzEDVlAE.jpg', 'Кресло игровое Ardor Gaming Chaos Guard 400M подарит геймеру комфорт и удобство, которые немаловажны для достижения побед при прохождении различных игр. Модель с эффектной черной расцветкой отличается хорошим ограничением по весу, достигающим 150 кг. Обивка выполнена из прочной и легкой в очистке экокожи, обеспечивающей креслу презентабельный внешний вид.', 'Конструкция данной модели предусматривает подголовник и подлокотники. Крестовина кресла изготовлена из пластика. Для удобства перемещения игровое кресло Ardor Gaming Chaos Guard 400M оснащено колесиками. Предусмотрена возможность регулировки высоты кресла в диапазоне от 1305 до 1210 мм.', 1, 1, NULL),
(53, 'Кресло игровое Iron Leaf 3K', '11799.00', '/uploads/yjc1Amzg.jpg', 'Кресло игровое ARDOR GAMING Iron Leaf 3K– надежная и элегантно выглядящая модель, которую можно настроить под требования любого геймера. Конструкция устройства предусматривает пластиковую крестовину с роликами, имеющими полиуретановые вставки.', 'С их помощью кресло можно легко перемещать по любому напольному покрытию, не царапая и не повреждая его. Выдерживает постоянную нагрузку до 150 кг.', 1, 1, NULL),
(72, 'Кресло игровое Chaos Guard 400M Black', '13999.00', '/uploads/3yW8epHp.jpg', 'Кресло игровое Ardor Gaming Chaos Guard 400M подарит геймеру комфорт и удобство, которые немаловажны для достижения побед при прохождении различных игр. Модель с эффектной черной расцветкой отличается хорошим ограничением по весу, достигающим 150 кг. Обивка выполнена из прочной и легкой в очистке экокожи, обеспечивающей креслу презентабельный внешний вид.', 'Конструкция данной модели предусматривает подголовник и подлокотники. Крестовина кресла изготовлена из пластика. Для удобства перемещения игровое кресло Ardor Gaming Chaos Guard 400M оснащено колесиками. Предусмотрена возможность регулировки высоты кресла в диапазоне от 1305 до 1210 мм.', 1, 1, NULL),
(73, 'Кресло игровое DXRACER OH/RE0/NE', '42990.00', '/uploads/xLZEhBL2.jpg', 'Игровые кресла DXRacer серии Racing – настоящий бестселлер на рынке игровых и офисных кресел. Созданные изначально для киберспортсменов и ярых геймеров, кресла серии R быстро завоевали популярность среди тех, кому приходится длительное время проводить за компьютером. Именно кресла серии Racing наиболее часто используются на киберспортивных турнирах, а также являются выбором киберспортивных команд в качестве своих профессиональных брендовых кресел.\r\n\r\nВ креслах DXRacer R-серии с комфортом разместятся даже широкоплечие люди выше среднего роста и любого телосложения. Подлокотники кресла, в зависимости от модели, регулируются в трех или четырех плоскостях: по высоте и в продольном направлении, а также поворачиваются вокруг оси на определенный угол. К креслу DXRacer серии R можно прикрепить специальный подлокотник, позволяющий пользоваться мышкой на его поверхности.', 'Racing - одна из самых ярких серий кресел DXRacer. Кресла серии R имеют большое множество цветовых исполнений: от классических черно-белых до смелых розовых и фиолетовых.\r\n\r\nКресла R-серии обтянуты износостойкими и воздухопроницаемыми материалами, приятными на ощупь: эко-кожей, эко-кожей с карбоновыми вставками или комбинацией из ткани и эко-кожи. В комплекте с креслом идут две подушки анатомической формы: под шею и под поясницу. Их можно с легкостью зафиксировать на спинке кресла на необходимой вам высоте.\r\n\r\nПомимо того, что спинка кресла DXRacer серии Racing имеет угол наклона до 170 градусов, кресло в зависимости от модели также оснащается одним из двух механизмов качания: Топ-Ган или мультиблок. Вся конструкция установлена на алюминиевой крестовине и оснащена пятью двухдюймовыми или трехдюймовыми прорезиненными колесиками. На конце каждого луча крестовины установлена рельефная накладка, предотвращающая скольжение ног.', 1, 4, NULL),
(74, 'Компьютерное кресло DXRacer I-DMC/IA233S/N', '54990.00', '/uploads/RQ4cf_8a.jpg', 'Компьютерное, игровое кресло DXRacer I-DMC/IA233S/N черного цвета серии Master Iron. Произведено на базе кресел серии Iron. Угол наклона спинки регулируется до 170 градусов.', 'Кресло DXRacer I-DMC/IA233S\\N оборудовано регулируемым поясничным упором и встроенным подголовником, настраиваемым по высоте. Подлокотники кресла можно настроить под себя в четырех направлениях.', 1, 4, NULL),
(75, 'Компьютерное Игровое кресло DXRACER OH/P08/NR', '28990.00', '/uploads/f0xmatFr.jpg', 'Компьютерное Игровое кресло DXRACER OH/P08/NR Черный, Красный\r\n\r\n \r\n\r\nСерия Peak – небольшие кресла DXRacer, которые идеально подойдут для людей среднего телосложения с ростом до 180см. Данной серии присущи все традиционные особенности кресел DXRacer – высокое качество, эргономичная форма сиденья и множество доступных регулировок, позволяющих подстроить кресло под себя и наслаждаться максимальным комфортом.\r\n\r\nВ зависимости от модели, подлокотники кресел данной серии регулируются в двух или в трех направлениях. Регулировка кресел по высоте осуществляется при помощи газоподъемного механизма 4 класса, а плавное качание с регулировкой жесткости обеспечивает механизм Топ-Ган. Две анатомические подушки позволяют снимать нагрузку с шеи и поясницы при длительной работе за компьютером.', 'Обивка кресел серии P выполнена из прочных и устойчивых к высоким нагрузкам и истиранию материалов. Множество цветовых решений данной серии позволяет подобрать кресло, которое будет отлично смотреться в любом окружении. В основании кресел данной серии располагается прочная и легкая нейлоновая крестовина. Мобильность кресла обеспечивают двухдюймовые ролики с полиуретановым покрытием, тихие и безупречно плавные.\r\n\r\nКомпьютерное Игровое кресло DXRACER OH/P08/NR Черный, Красный (P - Серия \" Peak \") это максимальный уровень комфорта.', 1, 4, NULL),
(76, 'Кресло игровое Everprof Lotus S1', '23250.00', '/uploads/4l1p28Zj.jpg', 'Everprof Lotus S1 Экокожа Белый (Игровые Кресла Lotus) это максимальный уровень комфорта.', 'Игровое кресло для геймеров «Everprof Lotus S6» станет отличным приобретением для любителей высокого уровня комфорта и стильного интерьерного решения.', 1, 5, NULL),
(77, 'Gun Shield 50M', '13499.00', '/uploads/YfpVEc_m.jpg', 'Кресло игровое Ardor Gaming Gun Shield 50M с высокой спинкой, поддерживающей спину и плечи; с подголовником, поясничной опорой и подлокотниками 3D.', 'Комфортное компьютерное кресло идеально подходит для использования как дома, так и в офисе. Его эргономичная конструкция адаптирована к форме тела, что помогает снизить напряжение мышц и усталость от продолжительной работы за компьютером.\r\nПодлокотники сконструированы таким образом, чтобы облегчить естественное положение сидящего пользователя, помогая выпрямить позвоночник вдоль подголовника для обеспечения максимального комфорта.', 1, 1, NULL),
(78, 'Кресло игровое Knight Thunder 5X Yellow', '19590.00', '/uploads/pr1Z-iQF.jpg', 'Кресло игровое Knight Thunder 5X  удобное кресло для работы или игр за компьютером дома и в офисе. ', 'Игровые кресла бренда Бюрократ, выполнен из качественных материалов, . Кресло игровое Knight Thunder 5X произведен в стране - Россия. ', 1, 6, NULL),
(79, 'Кресло игровое Knight Thunder 5X Blue', '19590.00', '/uploads/Hd3t--SS.jpg', 'Кресло игровое Knight Thunder 5X  удобное кресло для работы или игр за компьютером дома и в офисе. ', 'Игровые кресла бренда Бюрократ, выполнен из качественных материалов, . Кресло игровое Knight Thunder 5X произведен в стране - Россия. ', 1, 6, NULL),
(80, 'Кресло игровое Knight Rampart', '16190.00', '/uploads/aIGMPMWA.jpg', 'Игровое компьютерное кресло KNIGHT OUTRIDER LTD обладает стильным и практичным дизайном, позволяя с комфортом проводить время за играми. В конструкции сиденья вместо фанерного основания используются резинотканевые ремни. Новый чехол разработан так, что регулировка подушки по высоте остается возможной благодаря накладному карману в спинке. Геймерское кресло имеет регулировку высоты с помощью газлифта, а также механизм качания с регулировкой под вес и возможностью фиксации в вертикальном положении. Кресло способно выдерживать нагрузку до 150 кг. ', 'Обивка выполнена из высококачественной ткани, устойчивой к истиранию. Кресло установлено на прочную 5-лучевую основу из металла, в которую надежно закреплены колесики с мягким покрытием для паркета и ламината. В кресле имеются пластиковые подлокотники с регулировкой по высоте и поворотом влево-вправо. С помощью механизма реклайнер спинка отклоняется до 150 градусов. В комплекте две эргономичные подушки для поддержки шеи и поясницы. Соответствует стандарту BIFMA. Размеры: высота кресла - 127-136,5 см., высота сиденья - 45-54,5 см., ширина сиденья - 54 см., глубина сиденья - 41,5-49 см., высота спинки - 82 см., высота подлокотника - 17-24 см.', 1, 6, NULL),
(8999, 'Геймпад для консоли PS5 Sony DualSense Cosmic Red', '8999.00', '/uploads/lT1u62d9.png', 'Беспроводной контроллер DualSense используется с консолями PlayStation 5. Он оснащен мини-джойстиком и кнопкой-крестовиной. Корпус цвета White выполнен из ударопрочного пластика и металла. Парные приводы обеспечивают реалистичную физическую отдачу на действия пользователя.', 'Адаптивные триггеры позволят ощутить удары и другие игровые действия. Встроенный микрофон и разъем для гарнитуры можно использовать для общения в чате. Для выключения достаточно нажать на кнопку. Клавиша создания предназначена для записи и трансляции в прямом эфире игрового процесса.', 8, 3, NULL),
(9013, 'Геймпад для консоли PS5 Sony DualSense White', '8999.00', '/uploads/cDmkY1Bw.png', 'Беспроводной контроллер DualSense используется с консолями PlayStation 5. Он оснащен мини-джойстиком и кнопкой-крестовиной. Корпус цвета White выполнен из ударопрочного пластика и металла. Парные приводы обеспечивают реалистичную физическую отдачу на действия пользователя.', 'Адаптивные триггеры позволят ощутить удары и другие игровые действия. Встроенный микрофон и разъем для гарнитуры можно использовать для общения в чате. Для выключения достаточно нажать на кнопку. Клавиша создания предназначена для записи и трансляции в прямом эфире игрового процесса.', 8, 3, NULL),
(9015, 'Геймпад Microsoft Xbox Series S/X Robot White', '5686.00', '/uploads/DXcnu_NK.png', 'Беспроводной геймпад Microsoft подключается по Bluetooth к приставкам Xbox Series X, Xbox Series S, Xbox One, а также с компьютерам и планшетам с операционными системами Windows 10 и Android. ', 'Выбери устройство, на котором будешь играть, установи сопряжение – и вперёд, навстречу приключениям!', 8, 8, NULL),
(9016, 'Геймпад Microsoft Xbox Series S/X Carbon Black', '5728.00', '/uploads/6x_ZOuk5.png', 'Беспроводной геймпад Microsoft подключается по Bluetooth к приставкам Xbox Series X, Xbox Series S, Xbox One, а также с компьютерам и планшетам с операционными системами Windows 10 и Android. ', 'Выбери устройство, на котором будешь играть, установи сопряжение – и вперёд, навстречу приключениям!', 8, 8, NULL),
(9017, 'Геймпад для Switch Nintendo Switch Pro Controller', '7999.00', '/uploads/mT-Pnv3M.png', 'Геймпад для Switch Nintendo Switch Pro Controller — это игровой манипулятор, рассчитанный на беспроводное подключение. ', 'Модель оснащена кнопкой-крестовиной и двумя мини-джойстиками. Доступна функция обратной связи. В корпус, изготовленный из пластика, встроен аккумулятор. Вес — 246 г', 8, 9, NULL),
(9018, 'Геймпад Thunderobot G70 беспроводной', '7499.00', '/uploads/dOi9a6zK.png', 'Геймпад Thunderobot G70 JM03NJE03RU черного цвета обладает возможностью питания от аккумулятора и зарядки от USB-порта. Корпус выполнен из пластика. Длина комплектного кабеля — 1 м. У устройства 26 игровых клавиш. Для связи с персональным компьютером применяются интерфейсы Bluetooth, радиоканал и USB.', 'Прибор способен работать в режиме вибрации.\r\nУстройство обладает функцией обратной связи. Присутствуют два встроенных мини-джойстика. Геймпад оснащается сменным блоком ABXY. Ориентировочное время автономной работы составляет до 10 часов. В наличии встроенный держатель для смартфона.', 8, 10, NULL),
(9019, 'Геймпад A4Tech Bloody GPW50 Sports White', '3999.00', '/uploads/JUnkierW.png', 'Геймпад A4Tech GPW50 (Sports White) предназначен для использования с компьютерами, на которых установлена операционная система Windows, Android-девайсами, приставками PlayStation 3 и Nintendo Switch. ', 'Для подключения можно использовать беспроводной радиоканал со сверхнизкой задержкой на частоте 2,4 ГГц и кабель 1 м с USB-коннектором. Модель поддерживает функции Turbo и Clear. Асимметричные двигатели создают эффект вибрации. Для управления предусмотрено 13 клавиш, кнопка-крестовина и два мини-джойстика. Пластиковый корпус дополнен LED-индикаторами. Вес — 188 г.', 8, 11, NULL),
(9020, 'Геймпад A4Tech Bloody GPW50 Sports Red', '3999.00', '/uploads/galFYsbN.png', 'Геймпад A4Tech GPW50 (Sports White) предназначен для использования с компьютерами, на которых установлена операционная система Windows, Android-девайсами, приставками PlayStation 3 и Nintendo Switch. ', 'Для подключения можно использовать беспроводной радиоканал со сверхнизкой задержкой на частоте 2,4 ГГц и кабель 1 м с USB-коннектором. Модель поддерживает функции Turbo и Clear. Асимметричные двигатели создают эффект вибрации. Для управления предусмотрено 13 клавиш, кнопка-крестовина и два мини-джойстика. Пластиковый корпус дополнен LED-индикаторами. Вес — 188 г.', 8, 11, NULL),
(9022, 'Геймпад Razer Wolverine V2 Chroma White', '15999.00', '/uploads/0Ibc-tVB.png', 'Геймпад Razer Wolverine V2 Chroma (RZ06-04010200-R3M1) в белом корпусе — модель с эргономичным исполнением, совместимая с PC и консолями Xbox 360, Xbox One. Используется проводное соединение при помощи порта USB. Специальные резиновые рукоятки L-образной формы обеспечивают надежный захват и позволяют быстро и точно нажимать кнопки, чтобы оперативно отвечать на действия', 'соперников.\r\nЗа счет особой формы предусмотрен облегченный доступ к триггерам и бамперам. Механические кнопки обеспечивают сверхчувствительное срабатывание и мягкое нажатие. Каждое движение усиливается. Срок службы кнопок достигает 3 млн нажатий. Для расширения игровых возможностей геймпад снабжен дополнительными кнопками, среди которых предусмотрены две многофункциональные. Их при необходимости можно переназначить для получения более индивидуального стиля игры. Общее количество кнопок достигает 24. Длина кабеля составляет 3 м. Вес устройства — 270 г.', 8, 12, NULL),
(9023, 'Геймпад Razer Wolverine V2', '11499.00', '/uploads/a-oS7yvK.png', 'Перестрелки в современных шутерах и потасовки в файтингах требуют абсолютного контроля за ситуацией. Чтобы уверенно управлять героями, тебе пригодится геймпад Razer Wolverine V2, легко подключающийся к персональным компьютерам, консолям Xbox Series X|S. ПРОДВИНУТЫЕ ФУНКЦИИ Раскладка Razer Wolverine V2 напоминает классические контроллеры для Xbox.', 'При этом авторы установили L-образные рукоятки для удобного обхвата, нескользящие резиновые рукоятки, механические тактильные кнопки с высокой чувствительностью. При этом они демонстрируют достойную долговечность, выдерживая до 3 млн нажатий.\r\n\r\nНАЦЕЛЕН НА ПОБЕДУ\r\nНа передней панели расположились две многофункциональные кнопки, значения которых можно переназначить через приложение Razer Controller Setup. В шутерах также можно активировать специальный режим Hair Trigger Mode для повышенной скорострельности, чтобы тебе было проще нажимать на триггеры. Для подключения к консоли или компьютеру предусмотрен 3-метровый кабель.', 8, 12, 1),
(9024, 'Геймпад A4Tech Bloody GPW70 Sports Black', '4999.00', '/uploads/XhwaTDdG.png', 'Геймпад A4Tech GPW70 (Sports Black) совместим с компьютерами с операционной системой Windows, приставками Playstation 3 и Android-устройствами. Доступно два варианта подключения: беспроводной, через радиомодуль, на частоте 2,4 ГГц, и кабельный — через порт USB.', 'Модель оснащена асимметричными двигателями с двойной регулируемой вибрацией, 13 клавишами, кнопкой-крестовиной и мини-джойстиком. Предусмотрена поддержка функций Turbo и Clear для быстрой и точной стрельбы в шутерах. Материал, из которого изготовлен корпус, — пластик. Есть RGB-подсветка. Встроенный аккумулятор обеспечивает до 13 часов работы без подзарядки. Вес — 265 г.', 8, 11, NULL),
(9025, 'Геймпад A4Tech Bloody GPW70 Sports White', '4999.00', '/uploads/jTnMWw67.png', 'Геймпад A4Tech GPW70 (Sports White) совместим с компьютерами с операционной системой Windows, приставками Playstation 3 и Android-устройствами. Доступно два варианта подключения: беспроводной, через радиомодуль, на частоте 2,4 ГГц, и кабельный — через порт USB. ', 'Модель оснащена асимметричными двигателями с двойной регулируемой вибрацией, 13 клавишами, кнопкой-крестовиной и мини-джойстиком. Предусмотрена поддержка функций Turbo и Clear для быстрой и точной стрельбы в шутерах. Материал, из которого изготовлен корпус, — пластик. Есть RGB-подсветка. Встроенный аккумулятор обеспечивает до 13 часов работы без подзарядки. Вес — 265 г.', 8, 11, NULL),
(9026, 'Беспроводной геймпад Canyon CND-GPW3', '3999.00', '/uploads/V7ywq-1G.png', 'Геймпад Canyon CND-GPW3 позволяет тебе играть с комфортом на любой платформе. Он совместим с компьютерами под управлением Windows, Android-смартфонами, PlayStation 3 и портативной консолью Nintendo Switch. ЗАБУДЬ О ПРОВОДАХ Подключай геймпад на расстоянии до 10 метров и играй до восьми часов без перерыва.', 'После этого ты сможешь зарядить встроенный аккумулятор с помощью кабеля USB Type-C.\r\nОБРАТНАЯ СВЯЗЬ\r\nОщути отдачу оружия, удары врагов и столкновения машин! В геймпад встроены мощные вибромоторы, реагирующие на важные события в играх.', 8, 14, NULL),
(9028, 'Геймпад для консоли PS5 DualSense Midnight Black', '8999.00', '/uploads/WocIfr5p.png', 'Беспроводной контроллер DualSense используется с консолями PlayStation 5. Он оснащен мини-джойстиком и кнопкой-крестовиной. Корпус цвета White выполнен из ударопрочного пластика и металла. Парные приводы обеспечивают реалистичную физическую отдачу на действия пользователя.', 'Адаптивные триггеры позволят ощутить удары и другие игровые действия. Встроенный микрофон и разъем для гарнитуры можно использовать для общения в чате. Для выключения достаточно нажать на кнопку. Клавиша создания предназначена для записи и трансляции в прямом эфире игрового процесса.', 8, 3, NULL),
(9041, 'Игровая мышь Thunderobot ML702 ', '3799.00', '/uploads/cQK6XdQC.png', 'Игровая мышь Thunderobot ML702 со сроком службы 2 года перемещается со скоростью 101,6 см/сек. На модели расположено шесть кнопок, встроено семь режимов работы. Корпус пластиковый черного цвета с подсветкой и скроллером. Работает от аккумулятора Li-Polymer, зарядка осуществляется через кабель USB (включен в комплект).', 'Оптическое разрешение 8000 т/д возможно изменять. Используются батареи типа АА. Гарантия использования — 1 год. Общая масса — 96 г.', 2, 10, NULL),
(9042, 'Игровая мышь Razer DeathAdder Essential Black', '2299.00', '/uploads/PrryH_Qk.png', 'Игровая мышь Razer DeathAdder Essential пригодится и начинающему геймеру, и опытному киберспортсмену. В ней сочетаются эффективность, надёжность и удобство. ПРЕВОСХОДНАЯ ЭРГОНОМИКА Манипулятор отлично ложится в ладонь и не выскальзывает при резких движениях.', 'Благодаря тщательно продуманной форме рука не устаёт даже после напряжённого виртуального сражения, которое длится несколько часов.\r\n\r\nПОЛНЫЙ КОНТРОЛЬ\r\nПять кнопок мгновенно реагируют на нажатия. Каждая из них программируется независимо от остальных, и ты можешь настроить их так, как тебе нужно.\r\n\r\nСВОЁ РЕШЕНИЕ ДЛЯ КАЖДОЙ СИТУАЦИИ\r\nМаксимальное оптическое разрешение составляет 6400 dpi. Оно регулируется – меняй его, чтобы быстро просматривать карту или с ювелирной точностью наводить оружие на цель.', 2, 12, NULL),
(9043, 'Игровая мышь Razer DeathAdder Essential White', '2399.00', '/uploads/d8-i6Su_.png', 'Игровая мышь Razer DeathAdder Essential пригодится и начинающему геймеру, и опытному киберспортсмену. В ней сочетаются эффективность, надёжность и удобство. ПРЕВОСХОДНАЯ ЭРГОНОМИКА Манипулятор отлично ложится в ладонь и не выскальзывает при резких движениях.', 'Благодаря тщательно продуманной форме рука не устаёт даже после напряжённого виртуального сражения, которое длится несколько часов.\r\n\r\nПОЛНЫЙ КОНТРОЛЬ\r\nПять кнопок мгновенно реагируют на нажатия. Каждая из них программируется независимо от остальных, и ты можешь настроить их так, как тебе нужно.\r\n\r\nСВОЁ РЕШЕНИЕ ДЛЯ КАЖДОЙ СИТУАЦИИ\r\nМаксимальное оптическое разрешение составляет 6400 dpi. Оно регулируется – меняй его, чтобы быстро просматривать карту или с ювелирной точностью наводить оружие на цель.', 2, 12, NULL),
(9044, 'Игровая мышь A4Tech V8', '2199.00', '/uploads/iB37ZZYQ.png', 'Проводная игровая мышь A4Tech V8 обеспечивает максимальную четкость выстрелов. Возможность выбора режима стрельбы нажатием одной кнопки благодаря продвинутому программному обеспечению. 5 режимов разрешения. Удобное переключение режимов одним нажатием кнопки. 160 Кб встроенной памяти для хранения настроек в памяти мыши.', 'Время отклика до 1 мс. Минимальное время отклика дает преимущество в компьютерных играх, что позволяет моментально реагировать на действия противников во время игры. У обычной мыши время отклика составляет, в среднем, 16 мс , разные цвета колеса для разных настроек.', 2, 11, NULL),
(9045, 'Игровая мышь A4Tech Bloody V7 Black', '1999.00', '/uploads/uH1UC9rN.png', 'Геймерская мышь, которая даст тебе однозначное преимущество в игре, – это A4Tech Bloody V7 Black. Благодаря оптическому сенсору HoleLESS Engine ты получишь точное до долей миллиметра управление, а значит, сможешь реагировать быстрее и всегда быть впереди соперника. КОНТРОЛИРУЙ СИТУАЦИЮ Короткое время отклика мышки Bloody V7 позволит моментально реагировать.', 'Для сравнения: обычная мышь откликается за 16 мс, а A4Tech Bloody V7 Black – всего за 1 мс.\r\n\r\nТЕХНИЧЕСКАЯ СПЕЦИФИКА\r\nХромированные ножки X Glide устраняют трение, которое может замедлить тебя в бою. Система Ultra Core 4 & Macros даёт возможность исполнить одним кликом сложные комбинации. Встроенная память мыши Bloody V7, равная 160 Кб, позволит сохранить настройки удачных приёмов. Выбери из шести снайперских режимов подходящий, чтобы справиться с врагом на расстоянии.\r\n\r\nПРАКТИЧНЫЙ ДИЗАЙН\r\nЧтобы рука не скользила по bloody gun3 v7 и не сбивался прицел, верх мышки прорезинен. Ты будешь долго наслаждаться работой мыши A4Tech Bloody V7 Black, ведь срок службы кнопок – свыше десяти миллионов кликов.', 2, 11, NULL),
(9046, 'Игровая мышь Red Square O2 RS122', '2099.00', '/uploads/wOPwycGf.png', 'Проводная игровая мышь Red Square RS122 (RSQ-10022) весом 63 г выполнена в черно-бордовом цвете. Корпус изготовлен из пластика обладает множеством отверстий, снабжен отключаемой RGB-подсветкой и скроллером. Совместима с macOS и PC, подключается посредством порта USB. Разъем покрыт позолотой.', 'Мышь обладает шестью кнопками. Оптическое разрешение достигает 12400 т/д, возможно изменение разрешения. Сенсор расположен по центру прибора. Скорость перемещения — 559 см/сек, максимальное ускорение достигает 30 G. Максимальное размещение датчика DPI составляет 6200. Частота опроса достигает 1 КГц, переключатель расположен слева. Кабель с тканевой оплеткой обладает длиной 1,8 м.', 2, 18, NULL),
(9047, 'Игровая мышь Thunderobot ML703 Pro', '5499.00', '/uploads/bJgMCdL-.png', 'Игровая мышь Thunderobot ML703 Pro со сроком службы 2 года перемещается со скоростью 1016 см/сек. На модели расположено шесть кнопок, встроено восемь режимов работы. Корпус пластиковый черного цвета с подсветкой и скроллером. Работает от аккумулятора Li-Polymer, зарядка осуществляется через кабель USB (включен в комплект).', 'Оптическое разрешение 19000 т/д возможно изменять. Используются батареи типа АА. Гарантия использования — 1 год. Общая масса — 105 г.', 2, 10, NULL),
(9048, 'Игровая мышь Razer DeathAdder V2 X Hyperspeed', '5399.00', '/uploads/14uBVjta.png', 'Игровая мышь Razer DeathAdder V2 X Hyperspeed – обновленная версия классической модели DeathAdder, получившей десятки наград и пользующейся популярностью у киберспортсменов. Технология Razer HyperSpeed Wireless обеспечивает устойчивую беспроводную связь с минимальной задержкой, а семь кнопок на корпусе можно запрограммировать, чтобы повесить на них нужные функции.\r\n\r\nRazer DeathAdder V2 X Hyperspeed использует механические переключатели Razer второго поколения с позолоченными контактными точками и высокой выносливостью – они выдерживают до 60 млн нажатий.', 'Высокоточный оптический сенсор Razer 5G обеспечивает разрешение до 14 000 DPI.\r\n\r\nМышь работает от одной АА-батарейки, оснащена ножками из высококачественного фторопласта, а также встроенной памятью для хранения настроек. Она подключается к компьютеру по отдельному радиомодулю или Bluetooth.', 2, 12, NULL),
(9049, 'Игровая мышь A4Tech Bloody R90 Plus Naraka White', '4499.00', '/uploads/zX0rk8lr.png', 'Игровая мышь A4Tech Bloody R90 Plus Naraka White для правой руки удобно помещается в ладони и не выскальзывает за счет прорезиненного покрытия. Устройство подключается проводным способом или через Bluetooth и ловит сигнал на расстоянии до 10 м. Модель весом 111 г работает от аккумулятора, который можно зарядить с помощью кабеля в комплекте.', 'Корпус изготовлен из пластика, поэтому мышь легко перемещается и не требует сложного ухода. Благодаря подсветке модель с семью клавишами заметна в темноте.', 2, 11, NULL),
(9050, 'Игровая мышь Acer Nitro OMR304', '1999.00', '/uploads/vXNwjkaa.png', 'Беспроводная мышь Acer Nitro OMR304 — игровая модель с ярким дизайном, скоростью перемещения 76,2 см/с и высоким уровнем чувствительности. Аксессуар в корпусе из пластика габаритами 123х68х40 мм и весом около 90 г адаптирован под правую и левую руку. Размер встроенной памяти — 4 Мб.', 'Оптический сенсор с регулируемым разрешением (800, 1200, 2400, 3200 dpi) обеспечивает высокую точность и мгновенную реакцию на команды.\r\n\r\nМанипулятор взаимодействует с ПК с помощью миниатюрного ресивера, установленного в свободный компьютерный порт (радиосвязь на частоте 2,4 ГГц). Или подключается с помощью беспроводной связи Bluetooth дальностью действия до 10 м. Колесико прокрутки, клавиша изменения dpi и кнопки (основные правая, левая, боковые «Вперед», «Назад») обеспечивают удобство во время манипуляций, позволяют быстро реагировать на действия соперников.\r\n\r\nМышь получает питание от аккумуляторной батареи емкостью 400 мА*ч. Подзарядка — через провод длиной 40 см с разъемом USB Type-C. Режимы RGB-подсветки — динамичный, статичный (переключаются с помощью отдельной кнопки).', 2, 13, NULL),
(9051, 'Монитор AOC Gaming C27G2ZE 27\"', '22990.00', '/uploads/1m587I-Y.jpg', 'Гарантийный срок на игровой монитор AOC Gaming C27G2ZE равен целым 36 месяцам. Вес с подставкой равен 5.5 кг, а размеры - 612.37х398.6х227.4 мм. Для внешнего оформления производитель использовал элегантные оттенки красного и черного. Поскольку покрытие корпуса матовое, то следов от пальцев на поверхности оставаться не будет. Размер крепления VESA здесь 100х100. За питание отвечает внешний блок.', 'Энергопотребление игрового монитора AOC Gaming C27G2ZE равно 31 Вт, а при переходе в режим ожидания - менее 0.3 Вт. Здесь можно подключить наушники при помощи обычного входа на 3.5 мм. Реализована возможность просмотра видео в формате высокой четкости FULL HD (1080p). Предусмотрено наличие двух разъемов HDMI. Можно наклонять экран от -3.5 до +21.5 градуса, чтобы было удобнее взаимодействовать с моделью.', 3, 19, NULL),
(9052, 'Монитор Philips 242E1GAJ 23.8\"', '13770.00', '/uploads/TqAGMAxq.jpg', 'Великолепным изображением обладает монитор PHILIPS 242E1GAJ. Он оснащен экраном диагональю 23,8 дюйма, который имеет матрицу VA с разрешением 1920x1080 пикселей. Это позволяет создавать максимально реалистичную, яркую картинку с невероятной цветопередачей. Благодаря высокой частоте обновления в 144 Гц и минимальному периоду отклика в 4 мс обеспечивается комфорт во время игрового процесса или при просмотре видео, так как исключается задержка кадра во время динамичных сцен.', 'Функциональный монитор PHILIPS 242E1GAJ оборудован эргономичной подставкой с возможностью изменения угла наклона при эксплуатации. Широкие углы обзора по горизонтали и вертикали в 178 градусов способствуют удобному просмотру изображения с любого ракурса в помещении. На задней стороне монитора расположены разъемы HDMI, Display Port для подключения источников сигнала. Для мультимедиа используются встроенные динамики с насыщенным звучанием.', 3, 20, NULL),
(9053, 'Монитор ASUS TUF Gaming VG328H1B 31.5\"', '40075.00', '/uploads/ckhvOkuO.jpg', 'Гарантийный срок на игровой монитор ASUS TUF Gaming VG328H1B имеет внушительную продолжительность. Он составляет целых 36 месяцев. Вес равен 7.02 кг, а размеры без подставки - 709.75х426.15х87.33 мм. Модель выполнена в черном цвете, но благодаря матовой поверхности корпуса следы от пальцев на ней не остаются. Можно подвесить изделие, используя крепление VESA 100х100.', 'Производитель оснастил игровой монитор ASUS TUF Gaming VG328H1B внутренним блоком питания. В обычном режиме работы его энергопотребление равно 28 Вт, а в режиме ожидания составляет менее 0.5 Вт. Предусмотрен стандартный выход на наушники 3.5 мм. Есть один разъем HDMI. Можно поворачивать экран на 15 градусов в любую сторону. Также есть возможность наклона экрана в диапазоне от -5 до +23 градусов. Экран изогнутый, что позволяет погрузиться в атмосферу игры.', 3, 22, NULL),
(9054, 'Монитор LG UltraWide 34WP65C-B 34\"', '39990.00', '/uploads/p_OrPxwn.jpg', 'LG UltraWide 34WP65C-B — монитор для геймеров с изогнутым экраном, тонким корпусом, возможностью регулировки положения и широким набором настроек для погружения в мир гейминга и мультимедиа. Сертифицированные технологии защиты зрения снижают нагрузку на глаза даже при длительной работе за экраном. Подсветка без мерцания Flicker-Free минимизирует неприятные блики, позволяя играть без напряжения глаз даже в полной темноте.', 'Характеристики экрана\r\n\r\nБольшой экран с диагональю 34 дюйма, разрешением 3440x1440 и VA-матрицей с частотой обновления кадров 160 Гц позволяют просматривать и редактировать изображения и мультимедийные файлы, открывать одновременно несколько документов, запускать требовательные игры с динамичными, реалистичными кат-сценами. Технология FreeSync при поддержке графического процессора AMD устраняет искажение и подёргивание изображения, минимизируя задержки и повышая сглаживание для более плавной игры. Светодиодная подсветка монитора улучшает контрастность и цветопередачу, а широкие углы обзора 178° позволяют смотреть на происходящее на экране с любой точки обзора без потери качества и замыливания изображения. Изогнутый экран с поддержкой стандарта HDR10 и широким цветовым охватом создаёт яркую и насыщенную картинку.\r\n\r\nОсобенности\r\n\r\nСертифицированная технология защиты зрения снижает нагрузку на глаза, позволяя находиться перед монитором длительное время. Благодаря технологии Flicker-free отсутствует мерцание монитора: яркостью управляет фиксированный сигнал, что также защищает зрение и не позволяет глазам пользователя напрягаться, особенно если работать или играть приходится при недостаточном освещении.\r\n\r\nЭргономика монитора\r\n\r\nLG UltraWide 34WP65C-B можно поворачивать, регулировать его высоту и угол наклона, выбирая оптимальное расположение относительно пользователя для снижения нагрузки на глаза и мышцы шеи.\r\n\r\nМультимедиа\r\n\r\nМонитор поддерживает видео высокой чёткости благодаря формату Ultra HD 2K (1440p). Установка дополнительных акустических систем не требуется: в LG UltraWide 34WP65C-B встроены динамики мощностью 2х7 Вт с выходом на наушники. Разъём USB Type-C позволяет подключать дополнительные мобильные устройства, гаджеты, съёмные диски и флеш-карты для считывания данных с них напрямую.\r\n\r\nСообщить об ошибке в описании', 3, 21, NULL),
(9055, 'Монитор Acer Nitro XV272UVbmiiprzx 27\"', '36140.00', '/uploads/9GQYGYVL.jpg', '27-дюймовый монитор Acer Nitro XV272UPbmiiprzx обладает всеми необходимыми для продуктивной игры характеристиками. Поддержка монитором технологии DisplayHDR 400 предусматривает идеально точную передачу всех цветовых оттенков, благодаря чему изображение становится более реалистичным. IPS-матрица экрана обеспечивает высокую степень яркости и контрастности, воспроизводя более детализированную картинку. Кадры даже очень ресурсоемкой игры будут сменяться плавно, без искажения и зависания, благодаря технологии динамического обновления AMD FreeSync.', 'Трехсторонний безрамочный дизайн монитора Acer Nitro XV272UPbmiiprzx делает его стильным, а изображение на нем максимально объемным. На корпусе монитора имеются стандартные видеоразъемы для подключения его к ПК, а также USB-интерфейс, через который можно подключать напрямую периферийное устройство. Встроенные динамики мощностью 2 Вт обеспечивают довольно качественное звучание. Дополнительно есть разъем для наушников. Для комфортной игры или работы у монитора регулируется его высота, угол наклона и даже можно развернуть его на 90° (портретный режим).', 3, 13, NULL),
(9056, 'Монитор AOC Gaming U34G3XM/EU 34\"', '36340.00', '/uploads/y6_QHMkV.jpg', 'Монитор AOC U34G3XM разработан для увлекательного погружения в мир виртуальной реальности. На экране 34 дюйма с панелью VA 3440x1440 пикселей отображается реалистичная картинка с яркими и насыщенными цветами. Частота 144 Гц, временя отклика 1 мс и технология FreeSync Premium гарантируют передачу детализированной и плавной картинки в динамичных играх. ', 'Технологии защиты зрения и матовая антибликовая поверхность повышают комфорт просмотра. С боковых сторон AOC U34G3XM расположены 1 разъем DisplayPort 1.4, 2 видеовыхода HDMI 2.0, выход на наушники. Конструкция подставки позволяет регулировать угол наклона, изменять высоту и поворачивать экран.', 3, 19, NULL),
(9057, 'Монитор Acer Nitro XZ342CUPbmiiphx 34\"', '44990.00', '/uploads/bNfXFRcI.jpg', 'Высоким качеством изображения и полным погружением в виртуальную реальность игры порадует геймеров монитор Acer Nitro XZ342CUPbmiiphx. Отличается высокой четкостью, яркостью, реалистичной цветовой гаммой, прекрасной визуализацией игрового процесса. Обладает изогнутым экраном с радиусом изгиба 1500R диагональю 34\" с разрешением 3440x1440, матрицей VA и частотой обновления 144 Гц.\r\n\r\nВремя отклика составляет 1 мс, что обеспечивает практически мгновенное выполнение любых операций.\r\nЯркость экрана имеет показатель 400 кд/м2.\r\nПолностью погрузиться в игровой процесс помогает соотношение сторон экрана 21:9.\r\nПоддержка AMD FreeSync предотвращает зависание изображения, образование шлейфов, обеспечивает плавный игровой процесс.\r\nНасыщенность любого цвета, оттенка, тени помогает получить технология HDR.', 'Играть с комфортом\r\n\r\nМонитор Acer Nitro XZ342CUPbmiiphx обеспечивает максимальные углы обзора до 178 градусов, что позволяет воспринимать изображение без искажений. При установке устройства на подставке возможно регулирование угла поворота экрана на -180°/+180°, наклона от -5° до +35°, высоты на 130 мм. Технология HDCP предотвращает нежелательное копирование видеосигнала. Предусмотрены разъемы HDMI версии 2.0 и DisplayPort 1.4 для подключения к ПК и периферии. Глубокий, чистый звук обеспечивает стереосистема, состоящая из 2 встроенных динамиков общей мощностью 4 Вт. Блок питания – внешний.\r\n\r\n', 3, 13, NULL),
(9058, 'Монитор Philips Evnia Gaming 24M1N3200ZA 23.8\"', '19780.00', '/uploads/MW6ottHJ.jpg', 'Монитор Philips 24M1N3200ZA/00 диагональю 23.8 дюйма является оптимальным выбором для организации игрового процесса. За плавность изображения отвечают частота обновления 165 Гц и время отклика 1 мс. Эффект погружения обеспечивается за счет тонкой рамки и технологии Ultra Wide-Color. Четкость изображения достойна восхищения, ведь она составляет 1920x1080 пикс.', 'Технология защиты зрения и режим LowBlue призваны позаботиться о сохранности глаз при долгой работе за экраном. Наклон и высота экрана регулируются в зависимости от удобства пользователя. Монитор Philips 24M1N3200ZA/00 оснащен поворотной подставкой.', 3, 20, NULL),
(9059, 'Монитор AOC Gaming 24G2ZE 23.8\"', '20660.00', '/uploads/QgO2bJLM.jpg', 'Монитор AOC 24G2ZE/BK соответствует требованиям опытных геймеров, которым важны высокая четкость передачи изображения без размытия и широкие функциональные возможности. На экране 23.8 дюйма IPS FullHD отображается реалистичное изображение с точностью передачи деталей и оттенков. Отклик 0.5 мс и частота до 240 Гц помогают избежать искажения при воспроизведении динамичной графики. ', 'Из интерфейсов в AOC 24G2ZE/BK имеются один разъем DisplayPort, два разъема HDMI, а также выход для подключения наушников. Подставка предоставляет возможность регулировки угла наклона и тем самым позволяет настраивать положение экрана.', 3, 19, NULL),
(9060, 'Стол игровой EUREKA ERK-GIP-44B-V2', '29140.00', '/uploads/F0_KdbFk.jpg', 'Игровой стол EUREKA ERK-GIP-44B-V2 позволит сосредоточиться на действительно важных вещах. Персональные настройки RGB-подсветки позволят создать восхитительную игровую атмосферу - почувствуйте себя частью любимой игры. Предусмотрена гарантия от производителя на 12 месяцев.', 'Прочные металлические ножки с защитой от коррозии – максимальная устойчивость на любой поверхности.\r\nТекстурированная столешница из МДФ с карбоновым покрытием.\r\nСовременный дизайн и классический черный цвет для кабинета или игровой комнаты.\r\nУдобный подстаканник для бутылок с водой или пластиковых стаканов с кофе или чаем.\r\nБоковой двойной крючок для наушников.\r\nДержатель для геймпада с USB-портами для зарядки.\r\nБольше свободного пространства\r\n\r\nБлагодаря системе управления кабелями на игровом столе EUREKA всегда будет порядок. Столешница имеет несколько слотов для укладки кабелей под столом, что освобождает больше места для игровых девайсов.', 4, 23, NULL),
(9061, 'Стол игровой EUREKA ERK-GIP-P60B-V1', '27080.00', '/uploads/B9vH3FU2.jpg', 'Стол для компьютера Eureka GIP 60 Black ERK-GIP-P60B-V1 учитывает различные потребности, поэтому можно сосредоточиться на таких важных вещах, как учеба, работа, игры или отдых.\r\n\r\nEUREKA GIP 60 имеет широкую 60-дюймовую столешницу с поверхностью в стиле \"карбон\", что позволяет разместить на столе несколько игровых мониторов, Xbox, PS4 и другие игровые девайсы.', 'Благодаря специальной системе управления кабелями, на столе никогда не будет лишних проводов. Столешница оснащена несколькими слотами для укладки кабелей под столом, освобождая пространство для игровых девайсов.', 4, 23, NULL),
(9062, 'Стол игровой EUREKA ERK-GIP-P47B-V1', '25360.00', '/uploads/4yDVDkEV.jpg', 'Стол для компьютера Eureka GIP 47 Black ERK-GIP-P47B-V1 учитывает различные потребности, поэтому можно сосредоточиться на таких важных вещах, как учеба, работа, игры или отдых.', 'EUREKA GIP 47- это сверхустойчивый и прочный стол с гладким покрытием в стиле карбон. Благодаря специальной системе управления кабелями, на столе никогда не будет лишних проводов. Столешница оснащена несколькими слотами для укладки кабелей под столом, освобождая пространство для игровых девайсов.', 4, 23, NULL),
(9063, 'Стол игровой регулируемый GMNG GG-TB301', '43390.00', '/uploads/M4WdbsbJ.jpg', 'Игровой компьютерный стол с электронной регулировкой высоты GMNG GG-TB301 позволит быстро и легко изменять положение столешницы относительно пола. В данной модели есть множество интересных опций, которые способствуют созданию комфортных условия не только для игр и развлечений, но и для учебы и работы.\r\n\r\nЭлектроподъемник. Оснащенные системой электропривода ножки позволяют изменять высоту стола в пределах от 75 до 120 см без приложения физических усилий.\r\n\r\nЭлектронное управление. С помощью встраиваемого проводного пульта с сенсорными кнопками и цифровым дисплеем можно поднимать и опускать стол, а также установить его в одну из трех ранее записанных позиций одним нажатием кнопки.\r\n\r\nСтолешница 140х80 см. Габариты столешницы позволяют разместить на ней пару стандартных или один широкоформатный монитор на комфортном для глаз расстоянии. Окраска поверхности под карбон обеспечивает стильный внешний вид и практичность.', 'Умная RGB-подсветка. Размещающиеся по левому и правому краю стола блоки динамической подсветки придают ему оригинальный вид и позволяют лучше ощущать габариты в темноте. Подсветка способна реагировать на нажатие кнопки, музыку и другие звуки благодаря встроенному в столешницу датчику.\r\n\r\nКабель-каналы с заглушками. Два отверстия в столешнице с секционными заглушками позволяют эстетично проложить кабели. В корпусе одной из заглушек также скрывается кнопка для управления подсветкой.\r\n\r\nСъемные подстаканник и крюк для наушников. В комплектацию стола входят кронштейн для гарнитуры и держатель для стаканов и бутылок, которые можно использовать для размещения наушников. Аксессуары крепятся под столешницей и выдвигаются по мере необходимости.\r\n\r\nКорзина для сетевого фильтра. Благодаря специальному подвесному отсеку под столешницей провода питания не будут создавать помех под ногами.\r\n\r\nИгровой коврик в комплекте. Комплектный коврик увеличенного размера во всю глубину стола покрывает большую часть столешницы, обеспечивает максимально точное и плавное перемещение мыши и не позволит соскользнуть клавиатуре.\r\n\r\nРегулируемые опоры. Винтовые опоры помогут выставить стол точно по уровню на любой поверхности.\r\n\r\nИгровой стол для ПК GMNG GG-TB301 – мечта любого геймера. Надежный, просторный, устойчивый, способный подстраиваться под основные потребности пользователя, он позволит полностью сосредоточиться на любимом занятии и не отвлекаться по пустякам.', 4, 25, NULL),
(9064, 'Стол игровой EUREKA ERK-EDK-R1S', '25990.00', '/uploads/OHdyEO8D.jpg', 'Высокая функциональность стола игрового EUREKA R1 S является неоспоримым преимуществом. Геймер, купив изделие, непременно оценит его возможности. Помимо широкой столешницы, где можно удобно расположить необходимые устройства, аксессуары и литературу, в конструкции предусмотрена выдвижная ниша под клавиатуру. Сбоку находится металлическое приспособление под чашку. Между стойками большое пространство, где пользователь сможет поставить системный блок.', 'На практичный дизайн стола игрового EUREKA R1 S нельзя не обратить внимание, поэтому изделием активно интересуются геймеры, любители компьютерных игр. Регулируемые ножки, изготовленные из прочной, надежной стали обеспечивают возможность настроить положение столешницы под индивидуальные требования пользователя. Наличие оригинальной подсветки заметно улучшает внешний вид стола. Он превосходно смотрится в современном интерьере.', 4, 23, NULL),
(9065, 'Стол игровой ВИТАЛ-ПК Райдер 1500', '14270.00', '/uploads/DzkOxu3E.jpg', 'На столе игровом ВИТАЛ-ПК Райдер удобно располагать монитор, принтер и вспомогательные аксессуары - колонки, наушники, микрофон, джойстик. Широкая и гладкая столешница, изготовленная из ламинированной древесно-стружечной плиты, позволяет максимально эффективно организовать рабочее место. В свободное пространство органично впишется системный блок. С целью повышения эстетичности изделия кромки обрамлены специальным материалом красного цвета.', 'Практичный, широкий стол игровой ВИТАЛ-ПК Райдер красного и черного цветов приобретают геймеры, стримеры, обычные пользователи. Модель позиционируется высокой надежностью, устойчивостью к царапинам и другим повреждениям, длительным эксплуатационным сроком. Сборка выполняется простыми инструментами и не представляет серьезных трудностей. Размеры стола 150 х 76 х 90 см позволяют использовать его с наибольшей эффективностью.', 4, 24, NULL),
(9066, 'Стол компьютерный Iron shell 110', '16449.00', '/uploads/mPAK1Yy-.jpg', 'Стол компьютерный ARDOR GAMING Iron shell 110 – модель для геймеров. Он обеспечивает эргономичность в эксплуатации и позволяет разместить все необходимые компоненты ПК. Электронная панель управления предусматривает регулировку высоты.', 'Столешница с размерами 110x60 см изготовлена из МДФ с нескользящим покрытием. Металлический каркас выдерживает нагрузку до 80 кг. В конструкции стола ARDOR GAMING Iron shell 110 есть подстаканник и крючок для наушников. Специальные вырезы гарантируют аккуратную и удобную укладку всех необходимых кабелей.', 4, 1, NULL),
(9067, 'Стол компьютерный Force Armor 7K', '9999.00', '/uploads/IteP-X4d.jpg', 'Стол компьютерный ARDOR GAMING Force Armor 7K создан для поклонников гейминга. Благодаря эргономичному исполнению и прочной конструкции он обеспечивает удобное размещение монитора и других компонентов ПК. Стол оборудован металлическим каркасом с двумя устойчивыми опорами и столешницей из МДФ с размерами 140x60 мм черного цвета.', 'Под столешницей расположены подстаканник и крючок для крепления наушников. Специальные вырезы гарантируют аккуратную укладку кабелей. В комплекте со столом ARDOR GAMING Force Armor 7K поставляется коврик для мыши.', 4, 1, NULL),
(9068, 'Стол игровой EUREKA Z60', '19235.00', '/uploads/pjYoRKlk.jpg', 'Игровой стол Eureka Z60 Gaming Desk - идеальный выбор для вашего игрового пространства.', 'Игровой стол Z60 - это модернизированный старший брат нашего самого популярного стола Z1-S. Z60 имеет широкую 60-дюймовую столешницу с поверхностью в стиле \"карбон\", что позволяет разместить на столе несколько игровых мониторов, Xbox, PS4 и другие ваши игровые девайсы. Есть отверстия для аккуратного размещения кабеля.', 4, 23, NULL),
(9082, 'GM-XL Asia Dragon Black and White (XL)', '1299.00', '/uploads/GJjJPiec.png', 'Коврик ARDOR GAMING GM-XL Asia Dragon Black and White идеально подходит для совместного использования с игровой мышью. Аксессуар обладает огромными размерами на уровне 900x400x4 мм, что делает его очень удобным для свободного управления манипулятором. Выполненный в черно-белой расцветке, коврик обладает удобной толщиной 4 мм для работы и игр: кисть ложится на коврик, при этом, вы не ощущаете дискомфорта даже на протяжении долгих часов игры.', 'Лицевая сторона коврика ARDOR GAMING GM-XL Asia Dragon Black and White изготовлена из ткани и имеет покрытие Speed. Такое покрытие характеризуется гладкостью, благодаря чему подходит для игр, требующих высокой скорости перемещения мышки. Материалом подложки служит резина, поэтому изделие остается на месте в любых ситуациях, а вы можете сосредоточиться на важных игровых моментах.', 5, 1, NULL),
(9083, 'GM-XL Asia Dragon (XL)', '1099.00', '/uploads/BZ5KQOhA.png', 'Коврик ARDOR GAMING GM-XL Asia Dragon идеально подходит для совместного использования с игровой мышью. Аксессуар обладает огромными размерами на уровне 900x400x4 мм, что делает его очень удобным для свободного управления манипулятором. Разноцветный коврик обладает удобной толщиной 4 мм для работы и игр: кисть ложится на коврик, при этом, вы не ощущаете дискомфорта даже на протяжении долгих часов игры.', 'Лицевая сторона коврика ARDOR GAMING GM-XL Asia Dragon изготовлена из ткани и имеет покрытие Speed. Такое покрытие характеризуется гладкостью, благодаря чему подходит для игр, требующих высокой скорости перемещения мышки. Материалом подложки служит резина, поэтому изделие остается на месте в любых ситуациях, а вы можете сосредоточиться на важных игровых моментах.', 5, 1, NULL),
(9084, 'GM-XL Asia Tiger Black and White (XL)', '1299.00', '/uploads/Lwpymczz.jpg', 'Коврик ARDOR GAMING GM-XL Asia Tiger Black and White идеально подходит для совместного использования с игровой мышью. Аксессуар обладает огромными размерами на уровне 900x400x4 мм, что делает его очень удобным для свободного управления манипулятором. Выполненный в черно-белой расцветке, коврик обладает удобной толщиной 4 мм для работы и игр: кисть ложится на коврик, при этом, вы не ощущаете дискомфорта даже на протяжении долгих часов игры.', 'Лицевая сторона коврика ARDOR GAMING GM-XL Asia Tiger Black and White изготовлена из ткани и имеет покрытие Speed. Такое покрытие характеризуется гладкостью, благодаря чему подходит для игр, требующих высокой скорости перемещения мышки. Материалом подложки служит резина, поэтому изделие остается на месте в любых ситуациях, а вы можете сосредоточиться на важных игровых моментах.', 5, 1, NULL),
(9085, 'GM-XL Asia Tiger (XL)', '1099.00', '/uploads/1QdKWY2l.jpg', 'Коврик ARDOR GAMING GM-XL Asia Tiger идеально подходит для совместного использования с игровой мышью. Аксессуар обладает огромными размерами на уровне 900x400x4 мм, что делает его очень удобным для свободного управления манипулятором. Выполненный в азиатском стиле, коврик обладает удобной толщиной 4 мм для работы и игр: кисть ложится на коврик, при этом, вы не ощущаете дискомфорта даже на протяжении долгих часов игры.', 'Лицевая сторона коврика ARDOR GAMING GM-XL Asia Tiger изготовлена из ткани и имеет покрытие Speed. Такое покрытие характеризуется гладкостью, благодаря чему подходит для игр, требующих высокой скорости перемещения мышки. Материалом подложки служит резина, поэтому изделие остается на месте в любых ситуациях, а вы можете сосредоточиться на важных игровых моментах.', 5, 1, NULL),
(9086, 'XL Patron Red', '1299.00', '/uploads/JwpCjn5x.jpg', 'Коврик ARDOR GAMING XL Patron Red примечателен многоцветным рисунком. За счет размеров 900x400 мм он занимает большую часть столешницы. Благодаря гладкой тканевой поверхности Speed из полиэстера обеспечивается точное позиционирование курсора, легкое скольжение манипулятора в компьютерных играх и программах, а также приятные тактильные характеристики.', 'Резиновое основание предотвращает скольжение коврика при динамичных движениях мыши и запястья. Прошитые края увеличивают прочность и стойкость к повреждениям. Коврик ARDOR GAMING XL Patron Red облегчает управление манипулятором и защищает покрытие стола от повреждений.', 5, 1, NULL);
